mapper.select:
select
  id, menu_name, menu_code, parent_id, level, create_time, update_time
from menu


        and id = #{id}


        and menu_name = #{menuName}


        and menu_code = #{menuCode}


        and parent_id = #{parentId}


        and level = #{level}


        and create_time = #{createTime}


        and update_time = #{updateTime}


order by id desc
